    public static Integer getKeyWithLargestValue(HashMap<Integer, Integer> map) {


        // Extract entries
        HashMap.Entry<Integer, Integer> firstEntry = map.entrySet().iterator().next();
        if (map.size() == 1) {
            return firstEntry.getKey(); // Return the key if there's only one entry
        }

        // Get the second entry if there are two
        HashMap.Entry<Integer, Integer> secondEntry = map.entrySet().stream().skip(1).findFirst().get();

        // Compare values and return the key of the largest value
        return firstEntry.getValue() >= secondEntry.getValue() ? firstEntry.getKey() : secondEntry.getKey();
    }

    public int majorityElement(int[] nums) {
        HashMap<Integer, Integer> hashMap = new HashMap<>();

        Random random = new Random();
        double tests = nums.length < 100 ? 1000 : nums.length * Math.log(nums.length);

        int runs = nums.length < 100 ? 1000 : nums.length;
        for (int i = 0; i < runs; i++) {
            int pointer = nums[random.nextInt(nums.length)];
            int count = 0;

            for (int k = 0; k < tests; k++)
                if (nums[random.nextInt(nums.length)] == pointer)
                    count++;
            double rate = count / tests;
            //  System.out.println("rate " + rate);
            if (rate > 0.5) {
                //  System.out.println("pointer " + pointer);
                if (hashMap.containsKey(pointer)) {
                    hashMap.put(pointer, hashMap.get(pointer) + 1);
                } else {
                    hashMap.put(pointer, 1);
                }
            }
        }

        System.out.println(hashMap.entrySet());
        return getKeyWithLargestValue(hashMap);
    }


